package corejava;

public class IfElese_Maximu {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=200;
		int b=400;
		int c=600;
		if (a>b && a>c)
		{
			System.out.println(a +"is bigger");
		}
		 else if (b>a && b>c)
		{
			System.out.println(b+"is bigger");
		}
		 else
		{
			System.out.println(c+"is bigger");
		}

	}
	

}
